package Basics_of_java;

public class Switch_case {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub

		int day = 2;
		switch (day) {
		
		case 1: 
			System.out.println("Sunday");
			break;
			
		case 2:
			System.out.println("Monday");
			break;
			
		case 3:
			System.out.println("Monday");
			break;
			
		case 4:	
			System.out.println("Monday");
			break;
		default:
			System.out.println("Enter valid data");
			
			
		}
			
		}
		
	}


